import "./App.css";
import Search from "./components/Search";
import { useState } from "react";
import Results from "./components/Results";
import usePhotoSearch from "./components/usePhotoSearch";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles({
  bg: {
    backgroundImage:
      "url(/home/dci/Documents/React/PicPhotos/photos/src/images/greenleaves.jpeg)",
    backgroundSize: "contain",
    height: "30vh",
    border: "solid, 3px red",
  },
});

function App() {
  const [query, setQuery] = useState("");
  const [pageNumber, setPageNumber] = useState(1);
  const [searchOn, setSearchOn] = useState(false);
  const classes = useStyles();

  const [photos, hasMore] = usePhotoSearch(query, pageNumber, searchOn);
  return (
    <div>
      <header className={classes.bg}>
        <Search query={query} setQuery={setQuery} setSearchOn={setSearchOn} />
      </header>
      <main>
        <Results
          photos={photos}
          hasMore={hasMore}
          setPageNumber={setPageNumber}
        />
      </main>
    </div>
  );
}

export default App;
