import React, { useRef, useCallback } from "react";
import EachPhoto from "./EachPhoto";

export default function Results(props) {
  const { photos, hasMore, setPageNumber } = props;
  //when we rerender the component, everything change, but by using useRef(). This function return one object with property "current" with the value of
  const observer = useRef();

  //the function inside callback function will happen just when the variable inside of [] changes. Really similar to useEffect BUT IS CONNECT WITH SOME VARIABLE AND STORED INSIDE OF IT.
  //node refers to the <EachPhoto/> element
  const lastPhotoElementRef = useCallback((node) => {
    if (observer.current) {
      observer.current.disconnet();
    }
  }, []);

  return (
    <div className="card-columns">
      {props.photo.map((item, index) => {
        if (item.cover_photo) {
          if (index === photos.length - 1) {
            return (
              <div ref={lastPhotoElementRef}>
                <EachPhoto key={index} photo={item} />
              </div>
            );
          } else {
            return (
              <div ref={lastPhotoElementRef}>
                {" "}
                <EachPhoto key={index} photo={item} />
              </div>
            );
          }
        }
        return null;
      })}
    </div>
  );
}
